 export const chosenCourse = (course) => {
    return {
        type: 'CHOOSE',
        payload: course
    };
};

export const removeChosenCourse = () => {
    return {
        type: 'REMOVE'
    };
};

export const addChosenCourse = (course) => {
    return {
        type: 'ADD_CHOICE',
        payload: course
    };
};

export const deleteChosenCourse = (id) => {
    return {
        type: 'REMOVE_CHOICE',
        payload: id
    };
};

export const addPreference = (pref) => {
    return {
        type: 'ADD_PREFERENCE',
        payload: pref
    };
};

export const deletePreference = (id) => {
    return {
        type: 'DELETE_PREFERENCE',
        payload: id
    };
};

export const toggleReview = () => {
    return {
        type: 'TOGGLE_REVIEW'
    }
}

export const resetAll = () => {
    return {
        type: 'RESET_ALL'
    }
}

export const submitPreferences = () => async (dispatch, getState) => {
    const preferences = getState();
    fetch('http://localhost:1337/professor', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify({name: name, email: email, hasResponded: hasResponded, coursesAssigned: coursesAssigned, blocksAvailable: blocksAvailable, blocksAssigned: blocksAssigned, requirements: requirements})
      })
    console.log(preferences)
}
