import React from 'react';
// import './Admin.css';
import Navigation from './Navigation.js';
import { useState, useEffect } from 'react';

import { Link } from 'react-router-dom';

/**
 * TeacherUploader page to view and send responese
 * @param {*} props 
 */
function TeacherUploader(props)  {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [professors, setProfessors] = useState([])

  useEffect(() => {
    fetch('http://localhost:1337/professor'
    ).then(response=>response.json()
    ).then(data=>{ 
      setProfessors(data)
    });
  })

  const handleSubmit = (event) => {
    alert('Professor ' + name + ' was submitted');

    fetch('http://localhost:1337/professor', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify({name: name, email: email})
      }).then(function(response) {
        // return response.json();
      });

    fetch('http://localhost:1337/professor'
      ).then(response=>response.json()
      ).then(data=>{ 
        setProfessors(data)
        console.log(data)
      }
      );

    event.preventDefault();
  }

  function SideButton(props) {

    function handleClick(props) {
      console.log("aaa");
      document.querySelectorAll('.sidebar-button').forEach(button => {
        button.classList.remove('button-clicked');
      });
      document.getElementById(props.name).classList.add('button-clicked');
    }

    return (
      //TODO link causes the buttons to not highlight
      <Link to={props.link}>
        <button id={props.name} class="btn-light sidebar-button" onClick={() => handleClick(props)}>
          {props.name}
        </button>
      </Link>
    );
  }

  function handleClick(props) {
    console.log(props);
  }

  return (
    <div className="app">
      <div class="row">
        <div class="col-lg-2 col-md-0 col-sm-0 menu-bar align-items-end">
          <div>
            <Navigation />
            <Link to="">
              <button id="Home" class="btn-light sidebar-button" onClick={() => handleClick(props)}>
                Home
              </button>
            </Link>
            {/*<h3><SideButton name="Course Scheduling Platform" link="/"/> <div /></h3>
            <SideButton name="Academic Info" /> <div />
            <SideButton name="Teacher Info Uploading" link="/teacher-uploader" /> <div />
            <SideButton name="Teacher's Requirements" /> <div />
            <SideButton name="Teacher Reminder" link="/teacher-reminder" /> <div />
            <SideButton name="Manual Scheduling" link="/scheduling"/> <div />
            <SideButton name="Automatic Scheduling" /> <div />
            <SideButton name="About" /> <div /> */}
          </div>
        </div>
        <div class="col-lg-5 col-md-6 col-sm-6">
          <form onSubmit={handleSubmit}>
            <label>
              Name: <input type="text" value={name} name="name" onChange={e => setName(e.target.value)} /> <br></br>
              Email: <input type="text" value={email} name="email" onChange={e => setEmail(e.target.value)} /> <br></br>
              Courses Assigned (separated by semicolons, only one space in each course name between the department name and the course code e.g.: "COSI 011A", uppercase letters only): <input type="text" value={''} name="name"  /> <br></br>
              Blocks Available (separated by semicolons, no spaces, uppercase letters only): <input type="text" value={''} name="name" /> <br></br>
              Assigned Blocks: <input type="text" value={''} name="name"  /> <br></br>
            </label>
            <input type="submit" value="Submit" />
          </form>
        </div>
        <div class="col-lg-5 col-md-6 col-sm-6">
          Current list of teachers goes here
          <br></br>
          {professors.map(professor => <div>Name: {professor.name}, Email: {professor.email}</div>)}
        </div>
      </div>
    </div>
  )
}
