import React from 'react';
// import './Admin.css';
import Navigation from './Navigation.js';

/**
 * TeacherUploader page to view and send responese
 * //TODO create some kind of listview reading from a database
 */

class TeacherUploader extends React.Component {
  constructor(props) {
    super(props);
    this.state = { name: '' };
  }

  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value});
  }

  handleSubmit = (event) => {
    alert('A form was submitted: ' + this.state);

    fetch('http://localhost:1337/professor', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(this.state)
      }).then(function(response) {
        console.log(response)
        return response.json();
      });

    event.preventDefault();
}

  render() {
    return (
      <div className="app">
        <div class="row">
          <div class="col-lg-2 col-md-0 col-sm-0 menu-bar align-items-end">
            <Navigation />
          </div>
          <div class="col-lg-5 col-md-6 col-sm-6">
            <form onSubmit={this.handleSubmit}>
              <label>
                Name: <input type="text" value={this.state.value} name="name" onChange={this.handleChange} />
                Email:
                courses_assigned_id 'separated by semicolons, only one space in each course name between the department name and the course code e.g.: "COSI 011A", uppercase letters only',
                blocks_available 'separated by semicolons, no spaces, uppercase letters only'
                assigned_blocks
              </label>
              <input type="submit" value="Submit" />
            </form>
          </div>
          <div class="col-lg-5 col-md-6 col-sm-6">
            Current list of teachers goes here
          </div>
        </div>
      </div>
    );
  }
}

