import React from 'react';
import './Admin.css';
import Navigation from './Navigation.js';

/**
 * Admin page to view and send responese
 * //TODO create some kind of listview reading from a database
 */
const Admin = () => {

    function Row(props) {
      function ButtonSend(props){
        function SendEMail(infor, subject)
{
    var jMail = new ActiveXObject("Jmail.message");     
    jMail.Silent = true;
    jMail.Charset = "gb2312";    
    jMail.FromName = "sender"    //发件人
    jMail.From = " sender@163.com ";   //发送人的邮件地址
    jMail.AddRecipient(" accepter@163.com ");  //收件人的邮件地址
    jMail.Subject = subject;
    jMail.Body = infor;    
    jMail.MailServerUserName="发件人邮箱登陆名";
    jMail.MailServerPassWord="发件人邮箱登陆密码";        
    var ret = jMail.Send("smtp.163.com");    
    if(ret == false)
    {
        alert("fail");
    }
    else
    {
        alert("success");
    }
    jMail.Close();

}
        return(
          <input type="button" value="邮件" onclick="mailto:xxx@bai163.com"></input>
        );
      }
      return(
        <tr class={props.state}>
          <th scope="row">{props.first}</th>
          <td>{props.last}</td>
          <td>{props.email}</td>
          <td>{props.state == "table-success" ? "view" :
            props.state != "table-danger" ? "": "send reminder"}</td>
          <td>
            <ButtonSend name="Send"></ButtonSend>
          </td>
          <td>
            <button>Toggle</button>
          </td>
        </tr>
      );
    }

    var testElements = [];
    for (var i = 1; i < 100; i++) {
      testElements.push(<Row first="test" last="-"
        email="-" state = {i % 7 == 0 && (i - 1) % 3 == 0 ?
        "table-success" : i % 5 == 0  && (i - 2) % 3 == 0? "table-danger" : ""}></Row>);
    }

    return (
      <div className="app">
        {/* <form onSubmit={this.onSubmit}>
          <input type="text" placeholder="Name" ref="name" />
          <input type="text" placeholder="Email" ref="email" />
          <input type="submit" />
        </form> */}
        <div class="row">
          <div class="col-lg-2 col-md-0 col-sm-0 menu-bar align-items-end">
            <Navigation />
          </div>
          <div class="col-lg-5 col-md-6 col-sm-6">
            this is where the teachers table will go
            <table class="table">
              <thead class="thead-dark">
                <tr>
                  <th scope="col">First</th>
                  <th scope="col col-lg-4 col-md-0 col-sm-0">Last</th>
                  <th scope="col col-lg-4 col-md-0 col-sm-0">Email</th>
                  <th scope="col col-lg-4 col-md-0 col-sm-0">Response</th>
                  <th scope="col col-lg-4 col-md-0 col-sm-0">Send</th>
                  <th scope="col col-lg-4 col-md-0 col-sm-0">Toggle</th>

                </tr>
              </thead>
              <tbody>
                <Row first="Antonella" last="Dilillo"
                  email="dilant@brandeis.edu" state="table-success"></Row> 
                <Row first="Olga" last="Papaemmanouil"
                  email="papaemmanouil@brandeis.edu"></Row>
                <Row first="Michael" last="Golitsyn" state="table-danger"
                  email="golitsyn@brandeis.edu"></Row>
                {testElements}
              </tbody>
            </table>
          </div>
          <div class="col-lg-5 col-md-6 col-sm-6">

          </div>
        </div>
      </div>
    );
  }
export default Admin;
