import React, { Component } from 'react';
import './Admin.css';
import Navigation from './Navigation.js';


/**
 * Admin page to view and send responese
 * //TODO create some kind of listview reading from a database
 */

 const Admin = () => {

  function click(){
      console.log("hello");
  }


  class Row extends Component{
    constructor(props){
      super(props);
      this.state = {
        state: props.state,
        first: props.first,
        last: props.last,
        email: props.email,
      };
    }

    render(){
      return(
            <tr class={this.state.state}>
          <td><button onClick={() => click()}>Send</button> <button onClick={() => this.changeState}
          >Toggle</button></td>
          <th scope="row">{this.state.first}</th>
          <td>{this.state.last}</td>
          <td>{this.state.email}</td>
          <td>{this.state.state == "table-success" ? "view" :
            this.state.state != "table-danger" ? "": "send reminder"}</td>
        </tr>

      )
    }
  }

// function Row(props){
//       return(
//         <tr class={props.state}>
//           <th scope="row">{props.first}</th>
//           <td>{props.last}</td>
//           <td>{props.email}</td>
//           <td>{props.state == "table-success" ? "view" :
//             props.state != "table-danger" ? "": "send reminder"}</td>
//         </tr>
//       );
//   }



    var testElements = [];
    for (var i = 1; i < 100; i++) {
      testElements.push(<Row first="test" last="-"
        email="-" state = {i % 7 == 0 && (i - 1) % 3 == 0 ?
        "table-success" : i % 5 == 0  && (i - 2) % 3 == 0? "table-danger" : ""}></Row>);
    }

    return (
      <div className="app">
        {/* <form onSubmit={this.onSubmit}>
          <input type="text" placeholder="Name" ref="name" />
          <input type="text" placeholder="Email" ref="email" />
          <input type="submit" />
        </form> */}
        <div class="row">
          <div class="col-lg-2 col-md-0 col-sm-0 menu-bar align-items-end">
            <Navigation />
          </div>
          <div class="col-lg-5 col-md-6 col-sm-6">
            this is where the teachers table will go
            <table class="table">
              <thead class="thead-dark">
                <tr>
                  <th scope=" col-lg-4 col-md-0 col-sm-0"></th>
                  <th scope="col">First</th>
                  <th scope="col-lg-4 col-md-0 col-sm-0">Last</th>
                  <th scope=" col-lg-4 col-md-0 col-sm-0">Email</th>
                  <th scope=" col-lg-4 col-md-0 col-sm-0">Response</th>
                </tr>
              </thead>
              <tbody>
                  <Row first="Antonella" last="Dilillo"
                  email="dilant@brandeis.edu" state="table-success"></Row> 

                <Row first="Olga" last="Papaemmanouil"
                  email="papaemmanouil@brandeis.edu"></Row>
                <Row first="Michael" last="Golitsyn" state="table-danger"
                  email="golitsyn@brandeis.edu"></Row>
                {/* {testElements} */}
              </tbody>
            </table>
          </div>
          <div class="col-lg-5 col-md-6 col-sm-6">

          </div>
        </div>
      </div>
    );
  }
export default Admin;
